import React, { useState, useRef, useEffect } from 'react';
import * as THREE from 'three';
import { Canvas, useFrame } from '@react-three/fiber';
import './background.css';
import { DoubleSide, FrontSide } from 'three';
interface bgCanvas {
    darkMode: boolean;
}

export default function BackgroundCanvas(props:bgCanvas) {
    const { darkMode } = props

    // When forming a MESH with useFrame(), it must be its own component, outside of App()
    function Sphere(props: JSX.IntrinsicElements['mesh']) {
        const mesh = useRef<THREE.Mesh>(null!)
        useFrame((state, delta) => (mesh.current.rotation.x += 0.01))
        useFrame((state, delta) => (mesh.current.rotation.y += 0.01))
        return (
          <mesh
            {...props}
            ref={mesh}>
            <sphereGeometry args={[1,128,128]} /> {/* radius, widthSeg, heightSeg */}
            <meshNormalMaterial />
          </mesh>
        )
    }
    function Skeleton(props: JSX.IntrinsicElements['mesh']) {
        const mesh = useRef<THREE.Mesh>(null!)
        useFrame((state, delta) => (mesh.current.rotation.x -= 0.005))
        useFrame((state, delta) => (mesh.current.rotation.y -= 0.005))
        return (
            <mesh
                {...props}
                ref={mesh}>
                <icosahedronGeometry args={[1.5, 2]} />  {/* radius, detail */}
                <meshPhongMaterial color="#ffffff" wireframe={true} wireframeLinewidth={200} side={DoubleSide} />
            </mesh>
        )
    }
    function Plane(props: JSX.IntrinsicElements['mesh']) {
        const meshPlane = useRef<THREE.Mesh>(null!)
        useEffect(() => {
            meshPlane.current.rotation.x = Math.PI / 4 * -1
            console.log(meshPlane.current.geometry.attributes)
        })

        return (
            <mesh
                {...props}
                ref={meshPlane}>
                <planeGeometry attach="geometry" args={[100, 100]} />  {/* radius, detail */}
                <meshNormalMaterial attach="material" side={DoubleSide} transparent={true} opacity={0.3} />
            </mesh>
        )
    }


    const createBubbleGeo = () => {
        const bubbleGeo = <sphereGeometry args={[1,128,128]} />

    }

    function Test(props: JSX.IntrinsicElements['mesh']) {
        const mesh = useRef<THREE.Mesh>(null!)
        // Rotating animation
        useFrame((state, delta) => {
            mesh.current.rotation.x += 0.01
            mesh.current.rotation.y += 0.01
        })
        // Accessing vertices
        useEffect(() => {
            const { geometry } = mesh.current
            const { position } = geometry.attributes // vertices of geometry
        })
        return (
          <mesh
            {...props}
            ref={mesh}
            castShadow={true}
            receiveShadow={false}>
            <sphereGeometry args={[1,128,128]} /> {/* radius, widthSeg, heightSeg */}
            <meshStandardMaterial 
                color={0xB5D5FE}
                roughness={0.6} 
                metalness={0.2} 
                side={FrontSide} />
          </mesh>
        )
    }


    return (
        <div className='bg' id='canvas-container'>
            {/* default: camera={ fov: 75, near: 0.1, far: 1000, position: [0, 0, 5] } */}
            <Canvas>
                <ambientLight intensity={0.3}/>
                <pointLight intensity={0.8} position={[10, 10, 15]}/>
                {/* <Sphere position={[0,0,0]} /> */}
                <Skeleton position={[0,0,0]} />
                <Plane position={[0,-10,-100]} />
                <Test position={[0,0,0]} />
            </Canvas>
        </div>
    )
}
