import React from 'react';
import { motion } from 'framer-motion';
import Disk from '../../assets/Disk';
interface HomeText {
    darkMode: boolean;
    darkColor: string;
    lightColor: string;
}
interface AnimatedLetters {
    title: string;
    className: string;
}

const textBanner = {
    animate: { 
        transition: {
            delayChildren: 0.4,
            staggerChildren: 0.1
        }
    }
};

const textLetter = {
    initial: { y: 500, opacity: 0 },
    animate: {
        y: 0,
        opacity: 1,
        transition: {
            ease: [0.6, 0.01, -0.05, 0.95],
            duration: 1
        } 
    }
};


const AnimatedLetters = (props:AnimatedLetters) => {
    const {title, className} = props;
    return (
        <motion.span
            className={`home-text ${className}`}
            variants={textBanner}
            initial='initial'
            animate='animate'>

            {[...title].map( (letter) => (
                <motion.span
                    className="letter"
                    variants={textLetter}>
                    {letter}
                </motion.span>
            ) )}

        </motion.span>
    )}

export default function HomeText(props:HomeText) { 
    const {darkMode, darkColor, lightColor} = props;

    return(
        <motion.div className={`home-col ${darkMode && "dark"}`}>
            <motion.span>

            </motion.span>


            <Disk darkMode={darkMode} darkColor={darkColor} lightColor={lightColor} />
        </motion.div>
    )
}
