        useFrame((state, delta) => (mesh.current.rotation.y += 0.01))
        return (
          <mesh
            {...props}
            ref={mesh}>
            <sphereGeometry args={[1,128,128]} /> {/* radius, widthSeg, heightSeg */}
            <meshNormalMaterial />
          </mesh>
        )
