import React from 'react';
import { motion } from 'framer-motion';
import Disk from '../../assets/Disk';
interface HomeText {
    darkMode: boolean;
    darkColor: string;
    lightColor: string;
}
interface AnimatedLetters {
    title: string;
    className: string;
}

// Variants for framer-motion
const textBanner = {
    animate: { 
        transition: {
            delayChildren: 0.4,
            staggerChildren: 0.1
        }
    }
};
const textLetter = {
    initial: { y: 300, opacity: 0 },
    animate: {
        y: 0,
        opacity: 1,
        transition: {
            ease: [0.6, 0.01, -0.05, 0.95],
            duration: 1
        } 
    }
};

// Component for animated letter banner
const AnimatedLetters = (props:AnimatedLetters) => {
    const {title, className} = props;
    return (
        <motion.div
            className={`home-text ${className}`}
            variants={textBanner}
            initial='initial'
            animate='animate'>

            {[...title].map( (letter) => (
                <motion.span
                    className="letter"
                    variants={textLetter}>
                    {letter}
                </motion.span>
            ) )}

        </motion.div>
    )}

export default function HomeText(props:HomeText) { 
    const {darkMode, darkColor, lightColor} = props;

    return(
        <motion.div className={`home-col ${darkMode && "dark"}`}
            variants={textBanner}
        >
            <AnimatedLetters title={"HI, WELCOME"} className={"home-text-title"} />
            <AnimatedLetters title={"my name is Lawrence Han"} className={"home-text-intro"} />
            <AnimatedLetters title={"I am an engineer ........"} className={"home-text-passage"} />

            <Disk darkMode={darkMode} darkColor={darkColor} lightColor={lightColor} />
        </motion.div>
    )
}
