import React from 'react';
import { motion } from 'framer-motion';
interface Card {
    darkMode: boolean;
    key: number;
    title: string;
    text: string;
    image: string;
    blob: string;
}


export default function ProjectCard(props:Card) {
    const { darkMode, key, title, text, blob, image } = props;
    let odd:boolean = ( key % 2 === 0 )


    // Card is split into image section and text section
    // // 'odd' will alternate order of the two sections per card
    const CardImage = ({blob, image}) => {
        return (
            <div className="card-image">
                <img 
                    src={require(`./projectBlobs/${blob}`)}
                    className="card-blob"
                    alt="blob"
                />
                <img 
                    src={require(`./projectImages/${image}`)}
                    className="card-image"
                    alt="card-image"
                />
            </div>
        )
    }
    const CardText = ({title, text}) => {
        return (
            <></>
        )
    }

    // Returning card
    if (odd) {
        return (
            <motion.div className="project-card">
                <CardImage blob={blob} image={image} />
                <CardText title={title} text={text} />
            </motion.div>
        )
    } else {
        return (
            <motion.div className="project-card">
                <CardText title={title} text={text} />
                <CardImage blob={blob} image={image} />
            </motion.div>
        )  
    }
}
