                {/* <mesh position={[0,0,-1]}>
                    <boxGeometry args={[1,1,1]} />
                    <meshStandardMaterial color="#FFAE00" />
                </mesh> */}
                <ambientLight intensity={0.3}/>
                <pointLight intensity={1.0} position={[20, 20, 10]} />
                <Sphere position={[0,0,0]} />
            </Canvas>
        </div>
    )
}
