import React, {useState, useEffect, useCallback} from 'react';
import './App.css';
import Intro from './pages/intro/Intro'
import Home from './pages/home/Home'


export default function App() {
  // Handling entering the home page from the intro section
  const [enter, setEnter] = useState<boolean>(false);
  const handleEnter = useCallback(() => {
    setEnter(prevEnter => {
      console.log('in event handler')
      return prevEnter ? prevEnter : !prevEnter
    })
  }, []);
  useEffect(() => {
    window.addEventListener("click", handleEnter)
    console.log('in effect')
    if (enter) {
      console.log('Cleaning eventListener for entrance...')
      window.removeEventListener("click", handleEnter)
    }
  }, [enter])

  return (
    <>
      { enter ? <Home /> : <Intro /> }
    </>
  );
};
